update patients
set allergies = 'NKA'
where allergies is null;

select distinct (city) AS unique_city 
from patients
where province_id='NS';

select first_name,last_name,birth_date
FROM patients 
WHERE height>160 and weight>70;

select first_name,last_name,allergies
from patients 
where allergies is not null and city ='Hamilton';

select first_name,last_name,gender 
from patients 
where gender='M';

select patient_id , diagnosis from admissions 
group by patient_id , diagnosis 
having count(*)>1;

select province_id , sum(height)as total_height
from patients 
group by height having height between 7000 and 8000;

SELECT UPPER(last_name) || ',' || LOWER(first_name) AS full_name
FROM patients
ORDER BY LOWER(first_name) DESC;

SELECT MAX(weight) - MIN(weight) AS weight_difference
FROM patients
WHERE last_name = 'Maroni';

SELECT 
  ROUND((COUNT(CASE WHEN gender = 'M' THEN 1 END) * 100.0) / COUNT(*), 2) AS male_percentage
FROM patients;
